import React, { useEffect, useState } from 'react';
import ReactPaginate from 'react-paginate';
import IVacation from '../../../interfaces/Vacation';
import './VacationsList.css';

const VacationsList = (props: { isAdmin: boolean }) => {
  const [vacations, setVacation] = useState<IVacation[]>([]);
  const [currentPage, setCurrentPage] = useState(0);

  const handlePageChange = (data: any) => {
    setCurrentPage(data.selected);
  };

  useEffect(() => {
    const getVacations = async () => {
      try {
        const response = await fetch('http://localhost:8080/vacation/vacations');
        const vacationsResponse = await response.json();
        setVacation(vacationsResponse);
      } catch (err) {
        console.log(err);
      }
    };

    getVacations();
  }, []);

  const itemsPerPage = 10;
  const offset = currentPage * itemsPerPage;
  const paginatedVacations = vacations.slice(offset, offset + itemsPerPage);

  const renderVacationCards = () => {
    const rows: JSX.Element[] = [];
    let currentRow: JSX.Element[] = [];

    paginatedVacations.forEach((item, index) => {
      currentRow.push(
        <div
          key={index}
          className="vacation-card"
          style={{
            border: '1px solid black',
            borderRadius: '10px',
            width: '200px',
            backgroundColor: 'rgba(169,169,169,0.57)',
          }}
        >
          <h3>Destination: {item.destination}</h3>
          <img src={item.image} width={'150px'} alt={item.destination} />
          <p>Description: {item.description}</p>
          <p>Price: {item.price}</p>
          <p>Start Date: {`${item.startDate}`}</p>
          <p>End Date: {`${item.endDate}`}</p>
        </div>
      );

      if (currentRow.length === 5 || index === paginatedVacations.length - 1) {
        rows.push(
          <div className="vacation-row" key={rows.length}>
            {currentRow}
          </div>
        );
        currentRow = [];
      }
    });

    return rows;
  };

  return (
    <div className="VacationsList">
      <header>
        <ReactPaginate
          previousLabel={''}
          nextLabel={''}
          breakLabel={'...'}
          pageCount={Math.ceil(vacations.length / itemsPerPage)}
          marginPagesDisplayed={2}
          pageRangeDisplayed={5}
          onPageChange={handlePageChange}
          containerClassName={'pagination'}
          activeClassName={'active'}
        />
      </header>

      <div className="vacations-grid">
        {renderVacationCards()}
      </div>
    </div>
  );
};

export default VacationsList;
