import { useEffect, useState } from "react";
import IVacation from "../../../interfaces/Vacation";
import "./VacationsList.css";
import ReactPaginate from "react-paginate";

const VacationsList = (props: { isAdmin: boolean }) => {
  console.log(`props.isAdmin: ${props.isAdmin}`);
  const [vacations, setVacation] = useState<IVacation[]>([]);
  const [currentPage, setCurrentPage] = useState(0);
  const itemsPerPage = 10;
  const pageCount = Math.ceil(vacations.length / itemsPerPage);

  const getVacations = async () => {
    try {
      const response = await fetch("http://localhost:8080/vacation/vacations");
      const vacationsResponse = await response.json();
      console.log(vacationsResponse);
      setVacation(vacationsResponse);
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    getVacations();
  }, []);

  const handlePageChange = ({ selected }: { selected: number }) => {
    setCurrentPage(selected);
  };

  const displayVacations = vacations
    .slice(currentPage * itemsPerPage, (currentPage + 1) * itemsPerPage)
    .map((item, index) => (
      <div
        key={index}
        style={{
          border: "1px solid black",
          borderRadius: "10px",
          width: "200px",
          backgroundColor: "rgba(169,169,169,0.57)",
        }}
      >
        <h3>Destination: {item.destination}</h3>
        <img src={item.image} width={"150px"} alt={`Vacation ${index}`} />
        <p>Description: {item.description}</p>
        <p>Price: {item.price}</p>
        <p>Start Date: {`${item.startDate}`}</p>
        <p>End Date: {`${item.endDate}`}</p>
      </div>
    ));

  return (
    <div>
      <div className="VacationsList" style={{ display: "flex", flexDirection: "row", flexWrap: "wrap" }}>
        {displayVacations}
      </div>
      <div className="pagination-container">
        <ReactPaginate
          previousLabel={"Previous"}
          nextLabel={"Next"}
          breakLabel={"..."}
          breakClassName={"break-me"}
          pageCount={pageCount}
          marginPagesDisplayed={2}
          pageRangeDisplayed={5}
          onPageChange={handlePageChange}
          containerClassName={"pagination"}
          activeClassName={"active"}
        />
      </div>
    </div>
  );
};

export default VacationsList;
